import React, { Component } from "react";
import { Link } from "react-router-dom";
import avatar from "../../img/avatar.svg";
import { Newmember } from "../../Requests/Apirequests";
class Newregister extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      reg_no: "",
      webmail: "",
      dob: "",
      sex: "",
      dept: "",
      level: "",
      password: "",
      con_password: "",
      hall: "",
      room: "",
      reasons: "",
      error: {},
      newMember: {}
    };
  }

  // Creates a static property
  static defaultProps = {
    sex: ["Select gender", "Male", "Female"],
    level: ["Select level", "100", "200", "300", "400", "500"],
    dept: [
      "Select Department",
      "ACCOUNTING",
      "AGRICULTURAL ECONOMICS",
      "AGRICULTURAL ENGINEERING",
      "AGRICULTURAL EXTENSION AND RURAL DEVELOPMENT",
      "ANIMAL SCIENCE",
      "APPLIED BIOLOGY & BIOTECHNOLOGY",
      "AQUACULTURE & FISHERIES MANAGEMENT",
      "BANKING AND FINANCE",
      "BIOCHEMISTRY",
      "BUSINESS ADMINISTRATION",
      "CHEMICAL ENGINEERING",
      "CIVIL ENGINEERING",
      "COMPUTER SCIENCE",
      "CROP SCIENCE",
      "ECONOMICS",
      "ELECTRICAL AND INFORMATION ENGINEERING",
      "FOOD SCIENCE & NUTRITION",
      "GEOPHYSICS",
      "INDUSTRIAL CHEMISTRY",
      "INDUSTRIAL CHEMISTRY (MATERIAL SCIENCE & POLYMER CHEMISTRY)",
      "INDUSTRIAL MATHEMATICS",
      "INTERNATIONAL RELATIONS",
      "MASS COMMUNICATION",
      "MATHEMATICS (WITH COMPUTER SCIENCE)",
      "MATHEMATICS (WITH STATISTICS)",
      "MECHANICAL ENGINEERING",
      "MECHATRONICS ENGINEERING",
      "MICROBIOLOGY",
      "PHYSICS (WITH ELECTRONICS)",
      "PHYSICS (WITH RENEWABLE ENERGY)",
      "POLITICAL SCIENCE",
      "SOCIOLOGY"
    ],
    hall_male: [
      "Select Hall of Residence",
      "Daniel Hall",
      "Abraham Hall",
      "Joseph Hall",
      "Isaac Hall"
    ],
    hall_female: [
      "Select Hall of Residence",
      "Dorcas Hall",
      "Sarah Hall",
      "Deborah Hall",
      "Abigal Hall"
    ]
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  // Submit Function for the form
  submit = e => {
    e.preventDefault();
    if (
      this.state.name === "" ||
      this.state.reg_no === "" ||
      this.state.webmail === "" ||
      this.refs.dob.value === "" ||
      this.state.sex === "" ||
      this.state.dept === "" ||
      this.state.level === "" ||
      this.state.hall === "" ||
      this.state.room === "" ||
      this.state.password === "" ||
      this.state.con_password === "" ||
      this.state.reasons === ""
    ) {
      const err = {
        error: "All Fields are required"
      };
      this.setState({ error: err });
    } else if (this.state.password === this.state.con_password) {
      // var re = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])\w{8,}$/;
      if (this.state.password < 8) {
        const err = {
          error: "Password must be above 8 characters"
        };
        this.setState({ error: err });
      }

      // re = /[0-9]/;
      else if (!/[0-9]/.test(this.state.password)) {
        const err = {
          error: "Password must contain at least one number (0-9)"
        };
        this.setState({ error: err });
      }

      // re = /[a-z]/;
      else if (!/[a-z]/.test(this.state.password)) {
        const err = {
          error: "Password must contain at least one lowercase letter (a-z)"
        };
        this.setState({ error: err });
      }

      // re = /[A-Z]/;
      else if (!/[A-Z]/.test(this.state.password)) {
        const err = {
          error: "Password must contain at least one uppercase letter (A-Z)"
        };
        this.setState({ error: err });
      }

      // Check if the Password is the same with the name
      else if (this.state.password === this.state.name) {
        const err = {
          error: "Password must be the same character with your name"
        };
        this.setState({ error: err });
      }

      // Put the member in an Object
      else {
        const newMem = {
          name: this.state.name,
          reg_no: this.state.reg_no,
          webmail: this.state.webmail,
          dob: this.refs.dob.value,
          sex: this.state.sex,
          dept: this.state.dept,
          level: this.state.level,
          hall: this.state.hall,
          room: this.state.room,
          password: this.state.con_password,
